import { createGlobalStyle } from "styled-components";

export const GlobalStyles = createGlobalStyle`
    body{
        margin: 0;
        padding: 0;
        height: 100%;
        width: 100%;
        margin: 0 auto;
        text-align: center;
        font-family: 'Open Sans', sans-serif;
        font-size: 16px;
        background-color: #15202B;
    }

    img {
        width: 70%;
        max-width: 600px;
        object-fit: cover;
        filter: grayscale(1);
    }

    img:hover {
        filter: none;
        width: 100vw;
        transition: ease 1s;
    }

    h1 {
        font-family: 'Licorice';
        font-size: 3em;
    }

    a {
        text-decoration: none;
        color: #AAAAAA;
    }

    a:hover {
        color: #FFFFFF;
    }
`
