import styled from 'styled-components'

export const Container = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-around;
    height: 80px;
    margin: 0;
    background-color: #18191E;
    color: #8899A6;
`

export const Signature = styled.h1`
    margin: 0;
    color: white;
    padding: .1em;
    font-size: 3em;
    font-family: 'Licorice', 'Roboto Condensed', sans-serif;
`
export const MarqueeWrap = styled.div`
    max-width: 00px;
`
