import { createGlobalStyle } from "styled-components";

export const GlobalStyles = createGlobalStyle`
    body{
        margin: 0;
        padding: 0;
        height: 100%;
        width: 100%;
        margin: 0 auto;
        text-align: center;
        font-family: 'Roboto Condensed', sans-serif;
        font-size: 16px;
        background-color: #15202B;
    }

    img {
        width: 50%;
        object-fit: cover;
    }

    a {
        text-decoration: none;
    }
`
